insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2025:na:1'), (select id from public.item_parts where source_key = 'human:short:2025:na:1:part:a'), 'Dobbelt lipidlag. Polære og hydrofile (vand-elskende) hoveder vekselvirker med vand eller væsken udenfor cellen eller cellens indre, cytoplasma. Non-polære og hydrofobe haler vender mod og vekselvirker med hinanden i membranens indre. Vekselvirkningerne stabiliserer og holder membranen sammen. Cellemembranen består primært af fosfolipider, kolesterol og proteiner.', 'rawdata-kortsvar-v1', 'human:short:2025:na:1:part:a:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', '4bda9331b2f7c0dbf9d73f82c08d437949f2947c1d003439a733a5e89889ebe5') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2025:na:1'), (select id from public.item_parts where source_key = 'human:short:2025:na:1:part:b'), 'Vandmolekyler kan grundet deres størrelse til en vis grad diffundere over cellemembranen. Processen hedder osmose, hvor vand transporteres passivt gennem cellemembranen til den side, hvor opløsningen har højest osmolaritet (høj koncentration af stoffer, lav ”vandkoncentration”). Cellemembranens permeabilitet for vand øges dog markant via aquaporiner (vandkanaler), der er selektivt permeable for vand. Tætheden af aquaporinmolekyler i membranen styrer vandtransporten i mange celler. Processen svarer til faciliteret diffusion.', 'rawdata-kortsvar-v1', 'human:short:2025:na:1:part:b:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', 'eabee7a4618756f8868ae5ff7865714c864fd48d944a5a1d04736ada52e07265') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2025:na:1'), (select id from public.item_parts where source_key = 'human:short:2025:na:1:part:c'), 'Stabil koncentration af forskellige ioner i cytosolen kræver ionpumper, der kompenserer for ionlækager fra cellen, bl.a. forårsaget af natrium-koblet transport og kalium leak-kanaler. Na⁺/K⁺-pumpen opretholder lav Na⁺-koncentration inde i cellen ved at pumpe Na⁺ ud af cellen. Samtidig sikrer denne pumpe høj koncentration af K⁺ inde i cellen, da den pumper K⁺ ind i cellen. Både Na⁺ og K⁺ transporteres imod deres elektrokemiske gradienter. Det er en energikrævende proces, der kræver hydrolysering af ATP (primær aktiv transport). Der pumpes 3 natriumioner ud af cellen mod 2 kaliumioner ind i cellen ved hver pumpecyklus. Dermed er Na⁺/K⁺-pumpen hovedansvarlig for opretholdelsen af natriums og kaliums koncentrationsforskelle og elektrokemiske gradienter hen over membranen, men bidrager selv ikke til membranpotentialet.', 'rawdata-kortsvar-v1', 'human:short:2025:na:1:part:c:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', '8c8bcd1663020b3ff3adaf3a6a6363c20e4deaebcf610c6417301cc1363ab2a1') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
