insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2025:na:3'), (select id from public.item_parts where source_key = 'human:short:2025:na:3:part:b'), 'Nedenfor er angivet processen hvormed erytrocytter ødelægges, og hvorledes hæmoglobinets Fe recirkuleres. Efter ca 120 dage levetid vil erythrocytternes membraner miste elasticitet, hvorved de let beskadiges og brister, når de passerer kapillærerne. Dette finder særligt sted i milten, hvor kapillærerne er snævre.
insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2025:na:4'), (select id from public.item_parts where source_key = 'human:short:2025:na:4:part:a'), 'pH er 2–3 i ventriklen, når den indeholder føde. Føden har ofte en buffer-effekt, hvorved pH kan stige op mod ca. 4, særligt i fundus-regionen.', 'rawdata-kortsvar-v1', 'human:short:2025:na:4:part:a:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', 'a0ad957bc681e6a1db9bd45fb691daa46f7e28951b0053bf49b7cc6c3d62ac60') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
