insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2024:na:1'), (select id from public.item_parts where source_key = 'human:short:2024:na:1:part:b'), 'I selve glykolysen frigøres kun en lille del af energien i glukosemolekylet, idet der netto dannes to ATP-molekyler og to reducerede co-enzymer (NADH). Ved anaerob omsætning reduceres pyrodruesyre til mælkesyre, hvorved NAD gendannes, så glykolysen kan fortsætte, men uden yderligere ATP-gevinst. Ved aerob omsætning vil de to pyrodruesyremolekyler fra ét glukosemolekyle give yderligere ATP samt danne reducerede co-enzymer (NADH og FADH₂) i Krebs’ cyklus. Den efterfølgende oxidation af disse i elektrontransportkæden kan under optimale forhold give op til ca. 34 ATP yderligere. Det er tilstrækkeligt at angive, at Krebs’ cyklus danner reducerede co-enzymer, som øger ATP-dannelsen betydeligt.', 'rawdata-kortsvar-v1', 'human:short:2024:na:1:part:b:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', '07e9b8096108ecce78eb3fabd2763456d32b9be60adb0e2509eaf68774acdeb5') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
