insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2017:ordinær:1'), (select id from public.item_parts where source_key = 'human:short:2017:ordinær:1:part:p1'), 'Virkningen af en negativ feedback-løkke er homeostase, dvs. stabilisering af et system ved at bringe det tilbage mod et ønsket set-point efter en afvigelse. De principielle komponenter er receptorer, der måler systemets tilstand, et kontrolcenter der integrerer input og sender signaler, samt effektorer der ændrer systemets tilstand. Ved temperaturregulering sender termosensorer i huden og dybt i kroppen signaler til temperaturkontrolcentret i hypothalamus. Herfra sendes signaler til effektorer såsom svedkirtler og overfladiske hudkar samt tværstribet skeletmuskulatur. Ved lav kropstemperatur ses karkontraktion, nedsat svedproduktion og kulderystelser; ved høj temperatur ses karkarudvidelse og øget svedproduktion. Derudover indgår bevidst adfærd. Tilsammen bringes kropstemperaturen tilbage mod set-point.', 'rawdata-kortsvar-v1', 'human:short:2017:ordinær:1:part:p1:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', 'fc0eb11278b036ee9eec3eef70281741cb60a7ee7108bd225a27b9739ee7cc01') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
