insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2017:ordinær:3'), (select id from public.item_parts where source_key = 'human:short:2017:ordinær:3:part:p1'), 'En brystlobulus består af en række alveoli, som er sækformede terminale udvidelser af udførselsgange. Flere udførselsgange samles til ductus lactiferus, som munder på brystvorten. Alveolerne er under laktation dilaterede og består af et mælkeproducerende epitel omgivet af myoepiteliale celler, som er kontraktile og presser mælken ud. Når barnet sutter, sendes sensoriske signaler til hypothalamus, hvilket fører til frigivelse af prolaktin fra hypofysens forlap, som stimulerer mælkeproduktionen, samt oxytocin fra hypofysens baglap, som udløser kontraktion af de myoepiteliale celler og nedløb af mælken.', 'rawdata-kortsvar-v1', 'human:short:2017:ordinær:3:part:p1:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', '2399fb6d316fda4f1b8a337253cb5d4fb6648648a126be687107bf1dc3134ae6') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
