insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2017:ordinær:5'), (select id from public.item_parts where source_key = 'human:short:2017:ordinær:5:part:p1'), 'Tyndtarmens indre overflade er dækket af villi, som er 0,5–1,5 mm lange fingerformede udposninger. Villi er dækket af epitel bestående overvejende af absorptive celler med mikrovilli, som optager nedbrudte fødeelementer og producerer fordøjelsesenzymer, samt bægerceller, der producerer mucus som beskytter slimhinden. Derudover findes granulære celler og endokrine celler. Centralt i villus findes bindevæv med et kapillærnet til optagelse af vandopløselige stoffer samt et lymfekapillær (lacteal) til optagelse af fedtopløselige stoffer.', 'rawdata-kortsvar-v1', 'human:short:2017:ordinær:5:part:p1:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', 'f681e3a29cb861c2ecc69b3f5aa8ba620f97878a8d219ad073bfb4bbf4687978') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2017:ordinær:6'), (select id from public.item_parts where source_key = 'human:short:2017:ordinær:6:part:p1'), 'Nefronet starter med et nyrecorpuskel bestående af et kapillærnøgle, glomerulus, der forsynes af den afferente arteriole og tømmes via den efferente arteriole. Glomerulus er omgivet af Bowmans kapsel, som har et visceralt og et parietalt lag. Præurinen filtreres til rummet mellem lagene og ledes videre gennem proximale convolute tubulus, Henles slynge med tynde og tykke segmenter, distale convolute tubulus, som via det juxtaglomerulære apparat har kontakt til den afferente arteriole, og til sidst samlerøret.', 'rawdata-kortsvar-v1', 'human:short:2017:ordinær:6:part:p1:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', 'e8ac9b32903b5348512256af57c0307937f87b22a0a5767fe299376580351724') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2017:sygeeksamen:1'), (select id from public.item_parts where source_key = 'human:short:2017:sygeeksamen:1:part:p1'), 'Ved positiv feedback vil systemets reaktion på en forstyrrelse medføre, at systemet bevæger sig yderligere væk fra sit ligevægtspunkt (set-point). Positiv feedback er således en selvforstærkende proces. Ved fødslen vil barnets hoved mod slutningen af graviditeten strække livmoderen, særligt cervix. Dette stræk medfører via nerveforbindelser til centralnervesystemet frigørelse af oxytocin fra hypofysens baglap. Oxytocin stimulerer livmoderens muskulatur til kontraktion, hvilket presser barnets hoved hårdere mod cervix og forårsager yderligere stræk. Dette giver et endnu kraftigere signal til livmoderen om at kontrahere sig. Processen er selvforstærkende og fører i sidste ende til fødslen.', 'rawdata-kortsvar-v1', 'human:short:2017:sygeeksamen:1:part:p1:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', '334366a06000d629e95808a7566684dde512bac6bc27b6a72efcd81e999623c3') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2017:sygeeksamen:2'), (select id from public.item_parts where source_key = 'human:short:2017:sygeeksamen:2:part:p1'), 'Det olfaktoriske epitel er placeret i toppen af næsehulen. De lugtfølsomme celler, olfaktoriske neuroner, er placeret i slimhinden og sender en dendrit ud til epitelets overflade, hvor den ender i talrige cilier, som ligger i et tyndt slimlag. Duftstoffer opløses i slimlaget og stimulerer cilierne, hvilket udløser et aktionspotentiale. Signalet bevæger sig langs dendritten og videre centralt via neuronets axon, som passerer gennem den cribriforme plade i kraniets bund og fortsætter til bulbus olfactorius, hvor der dannes synapser. Herfra sendes signalet via tractus olfactorius til olfaktorisk cortex.', 'rawdata-kortsvar-v1', 'human:short:2017:sygeeksamen:2:part:p1:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', '34cb53ac3e89f5a09e113a4ded9b9035cafee1bebf42ec34d8478191d555cf00') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2017:sygeeksamen:3'), (select id from public.item_parts where source_key = 'human:short:2017:sygeeksamen:3:part:p1'), 'Ét glukosemolekyle nedbrydes først ved glykolyse til to molekyler pyruvat under dannelse af to ATP og to NADH. Da der ikke er ilt til stede, kan NADH ikke oxideres via elektrontransportkæden. I stedet reduceres pyruvat til mælkesyre, hvorved NAD⁺ gendannes og glykolysen kan fortsætte. Det samlede ATP-udbytte er lavt, ca. 2 ATP pr. glukose, sammenlignet med aerob forbrænding, hvor der dannes ca. 36–38 ATP pr. glukose.', 'rawdata-kortsvar-v1', 'human:short:2017:sygeeksamen:3:part:p1:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', 'b474463a67918ee37c8f21aa80646019abf1d7bb82fdbdb5bb00e34aca1b1e08') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2017:sygeeksamen:4'), (select id from public.item_parts where source_key = 'human:short:2017:sygeeksamen:4:part:p1'), 'Overskydende væske i interstitialrummet dræneres via lymfekar. Lymfekapillærer er blindt endende, relativt permeable kar, der optager overskydende vævsvæske. Endotelcellerne danner klaplignende strukturer, som sikrer ensrettet flow. Lymfekapillærerne samles til større lymfekar med egentlige klapper. Lymfen transporteres fremad ved kompression fra omgivende væv samt ved rytmisk kontraktion af karvæggen, som indeholder glat muskulatur. Til sidst tømmes lymfen i venesystemet.', 'rawdata-kortsvar-v1', 'human:short:2017:sygeeksamen:4:part:p1:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', '34e20b21f8dc8a17dc5955950732342df441e5d86a329bfb774b87f03ed4a7a8') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2017:sygeeksamen:5'), (select id from public.item_parts where source_key = 'human:short:2017:sygeeksamen:5:part:p1'), 'Blodkarvæggen består indefra af tunica intima, som består af et lag endotelceller på en basalmembran, evt. med lidt elastisk bindevæv. Tunica media består af glat muskulatur samt varierende mængder bindevæv, herunder collagen og elastin. Tunica adventitia består af bindevæv, som forankrer karret til det omgivende væv. Arterier har en tykkere væg end vener, især på grund af en kraftigere tunica media, hvilket gør dem i stand til at modstå højere tryk.', 'rawdata-kortsvar-v1', 'human:short:2017:sygeeksamen:5:part:p1:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', '98766f61769f8b332f8b1d69ef2849af843e8944864a38d5fe4bb787fb40c5e8') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2017:sygeeksamen:6'), (select id from public.item_parts where source_key = 'human:short:2017:sygeeksamen:6:part:p1'), 'Kraftudviklingen kan justeres ved summation og rekruttering. Ved summation øges kraften ved højere stimulationsfrekvens af den enkelte muskelfiber, da afslapningen mellem kontraktioner mindskes, indtil der opnås tetanus. Ved rekruttering øges kraften ved aktivering af flere motoriske enheder. Maksimal kraft opnås, når alle motoriske enheder i musklen er aktiveret.', 'rawdata-kortsvar-v1', 'human:short:2017:sygeeksamen:6:part:p1:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', '2468e29c6da9b6daabacff97aa320f95f1f4ba843f23a478e1b77f4039086ec6') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2018:ordinær:1'), (select id from public.item_parts where source_key = 'human:short:2018:ordinær:1:part:p1'), 'Ét glukosemolekyle nedbrydes først ved glykolyse til to molekyler pyruvat under dannelse af 2 ATP og to NADH. Da der ikke er ilt til stede, kan NADH ikke oxideres via elektrontransportkæden til NAD⁺. I stedet reagerer NADH med pyruvat og danner mælkesyre, hvorved NAD⁺ gendannes og glykolysen kan fortsætte. Det samlede ATP-udbytte er lavt, ca. 2 ATP pr. glukose, sammenlignet med aerob forbrænding, hvor der dannes ca. 36–38 ATP pr. glukose.', 'rawdata-kortsvar-v1', 'human:short:2018:ordinær:1:part:p1:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', '2f50335d2523c581780c5c19a2e6284a7274dabeb53d197e48e92aa44766b51f') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2018:ordinær:2'), (select id from public.item_parts where source_key = 'human:short:2018:ordinær:2:part:p1'), 'Sinusknuden i højre forkammer fungerer som hjertets pacemaker og udviser spontane depolariseringer med den højeste frekvens. Depolariseringen breder sig over begge atrier og forårsager deres kontraktion. Signalet når herefter atrioventrikulærknuden, hvor udbredelsen forsinkes, inden impulsen ledes videre via atrioventrikulærbundtet gennem det fibrøse skelet mellem atrier og ventrikler. Bundtet løber ned i septum, deler sig i et højre og venstre grenbundt og forgrener sig videre i Purkinjefibre, som fra apex breder sig opad i ventriklernes vægge og udløser deres kontraktion.', 'rawdata-kortsvar-v1', 'human:short:2018:ordinær:2:part:p1:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', 'a29f1f7135238e28e0545db29fd508ebd0298547bdcb0ed863b1571a2cf41e0b') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2018:ordinær:3'), (select id from public.item_parts where source_key = 'human:short:2018:ordinær:3:part:p1'), 'Det venøse afløb fra størstedelen af mave-tarmkanalen samt fra milt og pancreas samles i vena porta. Blodet indeholder vandopløselige stoffer og næringsstoffer optaget i tarmen. I leveren ledes blodet gennem leversinusoiderne, hvor det blandes med blod fra a. hepatica og kommer i tæt kontakt med hepatocytterne, som kan modificere blodets sammensætning. Blodet ledes derfra via centralvener til levervenerne og videre til vena cava inferior. Systemet er karakteriseret ved et ekstra kapillærnet indskudt mellem to vener, hvilket muliggør specifik behandling af blodet fra mave-tarmkanalen.', 'rawdata-kortsvar-v1', 'human:short:2018:ordinær:3:part:p1:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', '5cc0f925f68550cd3f450ca1f85cc258189ee647107bdf930ffb7696bf42a4f9') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2018:ordinær:4'), (select id from public.item_parts where source_key = 'human:short:2018:ordinær:4:part:p1'), 'Ved blodtryksfald frigives renin fra nyrerne. Renin omdanner angiotensinogen til angiotensin I, som i lungeendotelet omdannes af ACE til angiotensin II. Angiotensin II virker vasokonstriktorisk og stimulerer binyrebarken til sekretion af aldosteron, som øger renal retention af natrium og vand. Dette øger blodvolumen og bidrager samlet til at normalisere blodtrykket.', 'rawdata-kortsvar-v1', 'human:short:2018:ordinær:4:part:p1:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', '48a222dcf7f78d09fe3562aaa42132654d680343fefcd13fdaedb966d3c0ddc8') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2018:ordinær:5'), (select id from public.item_parts where source_key = 'human:short:2018:ordinær:5:part:p1'), 'Makroskopisk er tyndtarmen dækket af peritoneum og indeholder et lag longitudinelle og cirkulære muskelfibre. Mukosa danner cirkulære folder. Overfladearealet øges yderligere ved villi, som er 0,5–1,5 mm lange fingerformede projektioner. Hver villus indeholder en bindevævskerne med kapillærnet til optagelse af vandopløselige stoffer samt et blindt endende lymfekapillær (lacteal) til optagelse af fedtopløselige stoffer. De absorptive epitelceller har mikrovilli på den luminale overflade, hvilket samlet øger overfladearealet med ca. 600 gange.', 'rawdata-kortsvar-v1', 'human:short:2018:ordinær:5:part:p1:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', '44dc84fd0a0dd690d09bf30ae2dc207a10dece278a06f851afb396326225a388') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'human:short:2018:ordinær:6'), (select id from public.item_parts where source_key = 'human:short:2018:ordinær:6:part:p1'), 'Alveolen er et lille luftfyldt hulrum, hvor gasudvekslingen foregår. Den indre overflade består overvejende af fladt pladeepitel, dækket af en væskefilm. Der findes også celler, som producerer surfactant, samt makrofager. Den respiratoriske membran består fra luftsiden af et tyndt væskelag med surfactant, alveoleepitelet, epitelets basalmembran, et tyndt interstitium, endotelets basalmembran og endotelcellelaget i lungekapillæret.', 'rawdata-kortsvar-v1', 'human:short:2018:ordinær:6:part:p1:model_answer:rawdata-kortsvar-v1', 'human', 'rawdata-kortsvar', 'rawdata-kortsvar-v1', '7845eaea06402444bb514724d3066b836e6a8483e8c375aa4ed80e6ca807dab4') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
