insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'sygdomslaere:disease:reumaroid-artrit-leddegigt'), (select id from public.item_parts where source_key = 'sygdomslaere:disease:reumaroid-artrit-leddegigt:domain:treatment'), 'NSAID anvendes til kombineret smertelindring og en vis lokal dæmpning, disse har dog alvorlige bivirkninger fx blødning fra mavesærk og tarm. Binyrebakrhormon har effektiv virkning, men bivirkningerne afholder lægerne fra udpræget brug. Bivirkningerne kan være udtynding af væv, ændret fedtfordeling (måneansigt) og vægtøgning. Ikke-biologiske lægemidler anvendes til at dæmpe immunsystemet, hvilket på længere sigt er den vigtigste . Denne behandling er dog meget dyr, hvorfor den først anvendes, når anden behandling har vist sig at være utilstrækkelig. En operation og udskiftning af led ses kun i svære tilfælde. Oplysninger om sygdommen kan motivere patienten til at overholde behandlingsregimer. Ergoterapi lærer patienten at bruge svækkede led og opnå højere funktionsniveau.', 'rawdata-sygdomslaere-v1', 'sygdomslaere:disease:reumaroid-artrit-leddegigt:domain:treatment:model_answer:rawdata-sygdomslaere-v1', 'human', 'rawdata-sygdomslaere', 'rawdata-sygdomslaere-v1', '4f76446ccbe11f95096e9044bd66628469c8038325022fdca64b496b78253d02') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
