insert into public.item_model_answers (item_id, part_id, answer_text, version, source_key, source_type, source_system, source_version, content_hash) values ((select id from public.study_items where source_key = 'sygdomslaere:disease:osteoartrose-slidgigt'), (select id from public.item_parts where source_key = 'sygdomslaere:disease:osteoartrose-slidgigt:domain:pathogenesis'), 'Artrose opstår som slutprodukt af en multifaktoriel skade på ledbrusk og lednær knogle. Kemisk sker der en øges produktion af brusknedbrydende enzymer udskilt af cellerne i leddet. Et tiltagende tab af brusk, hvilket gør brusken mindre modstansdygtig for tryk. Først bliver det glatte lag slidt af, hvorefter der kan komme små revner i brusken og dele kan slides af.', 'rawdata-sygdomslaere-v1', 'sygdomslaere:disease:osteoartrose-slidgigt:domain:pathogenesis:model_answer:rawdata-sygdomslaere-v1', 'human', 'rawdata-sygdomslaere', 'rawdata-sygdomslaere-v1', 'e5257d05a9a88fd056297e789dfcb4afe39eeb35aa31141a7d80b218453f3f0f') on conflict (source_key) do update set answer_text = excluded.answer_text, version = excluded.version, source_type = excluded.source_type, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.item_model_answers.content_hash is distinct from excluded.content_hash;
