insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2024:na:3:part:b:asset:64b8bf6cca06'), 'caption', 'Illustrationen viser cyklussen af krydsbrodannelse mellem myosin og aktin i tværstribede skeletmuskler. Fire trin er vist: (1) Myosin frigiver ATP og binder til aktin, (2) ATP hydrolyseres til ADP og uorganisk fosfat (Pi), hvilket forbereder myosin til kraftslag, (3) Pi frigives, hvilket udløser kraftslaget, hvor myosin trækker aktinfilamentet, (4) ADP frigives, og myosin forbliver bundet til aktin indtil nyt ATP binder, hvilket starter cyklussen forfra. Gentagne cyklusser af disse krydsbrodannelser og kraftslag fører til muskelkontraktion ved at trække aktinfilamenterne mod midten af sarkomeren.', null, null, null, 'legacy', null, null, 'legacy', 'human:short:2024:na:3:part:b:asset:64b8bf6cca06:caption:legacy', 'figure-captions', 'figure-captions-v1', '34169fe80e525ff57e81e02c48676d827211a6a29d15b72f11f9391f05ca9169') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
