insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2024:na:5:part:b:asset:c2b2c459487f'), 'caption', 'Diagrammet viser reguleringen af udskillelsen af thyroideahormoner. Andre områder i hjernen påvirker hypothalamus, som udskiller TRH (thyrotropin-releasing hormone). TRH stimulerer hypofysens forlap til at udskille TSH (thyreoidea-stimulerende hormon). TSH stimulerer thyroidea til at producere og udskille T4 og T3 hormoner. T4 omdannes til T3 i målorganerne. Der er også negative feedback mekanismer, hvor T4 og T3 hæmmer udskillelsen af TRH og TSH.', null, null, null, 'legacy', null, null, 'legacy', 'human:short:2024:na:5:part:b:asset:c2b2c459487f:caption:legacy', 'figure-captions', 'figure-captions-v1', 'ed11ab0ef1b53825e796cbaba5599d6a40676b1673c8af2044e7bee52d35bc3c') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
