insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2021:na:1:part:p1:asset:a92d557df0d6'), 'caption', 'Illustrationen viser en celle med det ru (kornede) endoplasmatiske reticulum og Golgi-apparatet markeret. Det ru endoplasmatiske reticulum (blå struktur) er forbundet med cellekernen og har ribosomer på overfladen, hvilket indikerer dets rolle i proteinsyntese. Golgi-apparatet (rød struktur) er vist som en serie af membranøse sække, der modtager vesikler fra det ru endoplasmatiske reticulum, bearbejder og pakker proteiner, og sender dem videre ud af cellen. Illustrationens fokus på disse organeller og deres interaktion understøtter en forklaring af deres funktioner.', null, null, null, 'legacy', null, null, 'legacy', 'human:short:2021:na:1:part:p1:asset:a92d557df0d6:caption:legacy', 'figure-captions', 'figure-captions-v1', '415b23ee5ced4a3c934ff61c60acac56afdb9307426ea9777e986d9aaf7ca812') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
