insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2018:sygeeksamen:4:part:p1:asset:b9dd2e00149b'), 'caption', 'Billedet viser hjertets anatomi med en detaljeret illustration af blodets flow gennem hjertets kamre og kar. Det starter ved vena cava superior og inferior, går til højre atrium, passerer tricuspidalklappen til højre ventrikel, videre gennem pulmonal semilunarklappen til pulmonal trunk og pulmonalarterier, der fører blod til lungerne. Fra lungerne returnerer blodet via pulmonalvenerne til venstre atrium, passerer bicuspidalklappen til venstre ventrikel, og pumpes ud gennem aortaklappen til aorta og videre til kroppens væv. Diagrammet indeholder også labels for hjerteklapper og blodkar, og viser både systemisk og pulmonal cirkulation.', null, null, null, 'legacy', null, null, 'legacy', 'human:short:2018:sygeeksamen:4:part:p1:asset:b9dd2e00149b:caption:legacy', 'figure-captions', 'figure-captions-v1', 'af072fcaa0f8b5250f0d5934a5a7243951082dc651b6b03a527d16668c224b5f') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2018:sygeeksamen:5:part:p1:asset:725c3e2b49a9'), 'caption', 'Illustrationen viser, hvordan nerveceller i hypothalamus regulerer hormonsekretion i den forreste hypofyselap. Hypothalamiske neuroner frigiver stimulerende (grønne cirkler) og hæmmende hormoner (røde cirkler), som passerer gennem hypothalamohypofysial portalsystemet til den forreste hypofyse. Her forlader disse hormoner kapillærer og påvirker hypofyseceller til enten at stimulere eller hæmme frigivelsen af hypofysehormoner (gule firkanter). Disse hypofysehormoner transporteres derefter via blodet til målorganer eller endokrine kirtler (markeret med grøn pil). Diagrammet forklarer således, hvordan hypothalamus nerveceller kan stimulere eller hæmme hormonsekretion i den forreste hypofyse.', null, null, null, 'legacy', null, null, 'legacy', 'human:short:2018:sygeeksamen:5:part:p1:asset:725c3e2b49a9:caption:legacy', 'figure-captions', 'figure-captions-v1', '2135a3d334eca07a77773e93b959bbe009947cabbf85c0b47d216e9ec5c48745') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
