insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2025:na:3:part:a:asset:c7b4074bbf09'), 'audit', 'Illustrationen viser processen, hvor blodets ilttransport normaliseres efter hurtig opstigning til store højder. Først er der et normalt antal erytrocytter og reduceret ilttransport (1). Nyren øger udskillelsen af erythropoietin (2), som transporteres via blodet til knoglemarven (3). Dette stimulerer produktionen af flere erytrocytter, hvilket efter nogle dage fører til et forhøjet antal erytrocytter og normal ilttransport (4).', true, 0.95, '', 'legacy', null, null, 'legacy', 'human:short:2025:na:3:part:a:asset:c7b4074bbf09:audit:legacy', 'figure-audit', 'figure-audit-v1', '30e5e6cdc70ed21b4f1bb7333b8189b63a09cba5ecdb59bdb024c4867cf876b1') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
