insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2025:na:5:part:d:asset:42918f902e98'), 'audit', 'Diagrammet viser stimuli, der stimulerer udskillelsen af ADH (antidiuretisk hormon). Øget osmolaritet, tørst og nedsat blodvolumen fungerer som positive stimuli, der aktiverer ADH-producerende neuroendokrine celler i hypothalamus. ADH opbevares i vesikler og udskilles fra hypofysens baglap ved eksocytose. ADH øger vandresorptionen i nyrerne, hvilket normaliserer osmolaritet og blodvolumen.', true, 0.95, 'Diagrammet beskriver kun stimuli og udskillelse af ADH, men nævner ikke eksplicit det andet hormon, som spørgsmålet refererer til. Hvis spørgsmålet omhandler to hormoner, mangler information om det andet hormon.', 'legacy', null, null, 'legacy', 'human:short:2025:na:5:part:d:asset:42918f902e98:audit:legacy', 'figure-audit', 'figure-audit-v1', '8c5ef4eb94e530673252355c487630a4144405577baea27e3f21add11345c95f') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2025:na:5:part:d:asset:a43aaf2e1ab9'), 'audit', 'Diagrammet viser stimuli, der stimulerer udskillelsen af oxytocin. Berøringsfølsomme sanseceller i brystvorterne og strækfølsomme sanseceller i uterus aktiverer oxytocinproducerende neuroendokrine celler. Oxytocin udskilles ved eksocytose fra hypofysens baglap og påvirker glatte muskelceller i mælkekirtlerne og uterus, hvilket presser mælk ud og øger veaktiviteten.', true, 0.95, 'Diagrammet beskriver kun stimuli og udskillelse af ét hormon, oxytocin, ikke to hormoner som nævnt i spørgsmålet.', 'legacy', null, null, 'legacy', 'human:short:2025:na:5:part:d:asset:a43aaf2e1ab9:audit:legacy', 'figure-audit', 'figure-audit-v1', '359bc628cd14ce4b227e741b8dac757fa8f710a1db53753256a7f2d4002fb2e0') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2025:na:6:part:a:asset:00223d2de66d'), 'audit', 'Billedet viser en nervecelleende (præsynaptisk membran) og en postsynaptisk membran adskilt af en synapsespalte. Det illustrerer processen, hvor et aktionspotentiale (1) fører til åbning af spændingsstyrede Ca2+-kanaler (2), hvilket medfører Ca2+-indstrømning (3). Dette udløser Ca2+-afhængig eksocytose af vesikler med neurotransmittere, som diffunderer over synapsespalten (4). Neurotransmitterne binder til receptorer på den postsynaptiske membran (5), hvilket åbner ligandstyrede ionkanaler og tillader Na+-indstrømning (6), der resulterer i depolarisering af den postsynaptiske nervecelle. Neurotransmitterne fjernes derefter fra synapsespalten (7).', true, 0.95, '', 'legacy', null, null, 'legacy', 'human:short:2025:na:6:part:a:asset:00223d2de66d:audit:legacy', 'figure-audit', 'figure-audit-v1', '390ba1a871be8b19a46012ff66fe04f92be4e1661f729021ec955b3513e997aa') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2024:na:2:part:a:asset:0c4242f25feb'), 'audit', 'Billedet viser en sekvens i fire trin, der illustrerer befrugtningen af en ægcelle. Først binder sædcellen til zona pellucida, som er et beskyttende lag omkring ægget. Dernæst sker den akrosomale reaktion, hvor enzymholdige vesikler frigives for at nedbryde zona pellucida. Tredje trin viser sædcellens penetration gennem zona pellucida. Til sidst fusionerer sædcellens membran med oocyttens membran, og sædcellens kerne optages i oocytten, hvilket markerer befrugtningen. Illustrationerne inkluderer også granulosa-celler omkring ægget og oocyttens membran og kerne.', true, 0.95, '', 'legacy', null, null, 'legacy', 'human:short:2024:na:2:part:a:asset:0c4242f25feb:audit:legacy', 'figure-audit', 'figure-audit-v1', '63c0ca08d0c5666632c560c4dbb45b6ba2eb7594fd58346107195ddf7186e0ea') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2024:na:3:part:b:asset:64b8bf6cca06'), 'audit', 'Illustrationen viser cyklussen af krydsbrodannelse mellem myosin og aktin i tværstribede skeletmuskler. Fire trin er vist: (1) Myosin frigiver ATP og binder til aktin, (2) ATP hydrolyseres til ADP og uorganisk fosfat (Pi), hvilket forbereder myosin til kraftslag, (3) Pi frigives, hvilket udløser kraftslaget, hvor myosin trækker aktinfilamentet, (4) ADP frigives, og myosin forbliver bundet til aktin indtil nyt ATP binder, hvilket starter cyklussen forfra. Gentagne cyklusser af disse krydsbrodannelser og kraftslag fører til muskelkontraktion ved at trække aktinfilamenterne mod midten af sarkomeren.', true, 0.95, '', 'legacy', null, null, 'legacy', 'human:short:2024:na:3:part:b:asset:64b8bf6cca06:audit:legacy', 'figure-audit', 'figure-audit-v1', '30a5a82991554222e3b871ea3a03fd4486c19137bf3f2bf55955b663be6641bf') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2024:na:4:part:a:asset:be4d617c86b5'), 'audit', 'Diagrammet viser processen for dannelse, modning og aktivering af lymfocytter. Umodne lymfocytter dannes i knoglemarven og modnes enten til T-lymfocytter i thymus eller til B-lymfocytter i knoglemarven. De modne T- og B-lymfocytter bevæger sig til sekundære lymfoide organer, hvor de aktiveres ved genkendelse af epitoper. Aktiveringen fører til klonselektions- og klonekspansionsprocesser, som resulterer i dannelse af T-angrebsceller, T-hjælpeceller, plasmaceller og hukommelsesceller, der er specifikke for epitoperne.', true, 0.95, 'Diagrammet er detaljeret og dækker alle trin i dannelse, modning og aktivering af lymfocytter som efterspurgt. Ingen væsentlige mangler fundet.', 'legacy', null, null, 'legacy', 'human:short:2024:na:4:part:a:asset:be4d617c86b5:audit:legacy', 'figure-audit', 'figure-audit-v1', '7e41beb800fded3eab2e819a84106af6acc3b621fa7bad30309643e6a444be0d') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2024:na:5:part:a:asset:fe66ec69a7db'), 'audit', 'Diagrammet viser processen hvor jodid (I⁻) i blodet transporteres ind i follikelcellen via en Na⁺-I⁻ symporter (1). Inde i cellen oxideres jodid til jod (I⁰) ved hjælp af enzymet thyroperoxidase (TPO) og H2O2 (2). Jodet inkorporeres i thyreoglobulin i kolloidet (3), hvor det danner joderet thyreoglobulin. Dette optages tilbage i follikelcellen via endocytose (4), hvor lysosomer nedbryder thyreoglobulinet og frigiver T3 og T4 hormonerne (5). Disse hormoner frigives til kapillærerne og bindes til thyroxinbindende globulin (TBG) i blodet (6). Diagrammet illustrerer således tydeligt syntese og udskillelse af T3 og T4 fra jodid i blodet.', true, 0.95, '', 'legacy', null, null, 'legacy', 'human:short:2024:na:5:part:a:asset:fe66ec69a7db:audit:legacy', 'figure-audit', 'figure-audit-v1', '5d85358818ba6f827fcb94523b6b49351aa82aff69a128aef7e6c89fe813d5f8') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2024:na:5:part:b:asset:c2b2c459487f'), 'audit', 'Diagrammet viser reguleringen af udskillelsen af thyroideahormoner. Andre områder i hjernen påvirker hypothalamus, som udskiller TRH (thyrotropin-releasing hormone). TRH stimulerer hypofysens forlap til at udskille TSH (thyreoidea-stimulerende hormon). TSH stimulerer thyroidea til at producere og udskille T4 og T3 hormoner. T4 omdannes til T3 i målorganerne. Der er også negative feedback mekanismer, hvor T4 og T3 hæmmer udskillelsen af TRH og TSH.', true, 0.95, '', 'legacy', null, null, 'legacy', 'human:short:2024:na:5:part:b:asset:c2b2c459487f:audit:legacy', 'figure-audit', 'figure-audit-v1', '7c5d9c5265bea8bf2ba70a1b3d92c393bb65fdc786efb27f1dd37dd4f65bcee7') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2024:na:6:part:a:asset:0c1e39a67120'), 'audit', 'Billedet viser to diagrammer af nyrernes arterioler og glomeruluskapillærer. I det øverste diagram (a) er den afferente arteriole konstrikeret, hvilket fører til nedsat hydrostatisk tryk og blodgennemstrømning i glomeruluskapillærerne, hvilket resulterer i nedsat filtration. I det nederste diagram (b) er den efferente arteriole konstrikeret, hvilket øger det hydrostatiske tryk i glomeruluskapillærerne, men blodgennemstrømningen falder, og resultatet er næsten uændret filtration.', true, 0.95, 'Ingen væsentlige problemer. Diagrammet illustrerer klart hvordan konstriktion af afferente og efferente arterioler påvirker filtration og blodgennemstrømning i nyrerne, som efterspurgt i spørgsmålet.', 'legacy', null, null, 'legacy', 'human:short:2024:na:6:part:a:asset:0c1e39a67120:audit:legacy', 'figure-audit', 'figure-audit-v1', '061f95e5b50c969a33916356f0b893d33313784e22397dd379790ecbab3e43c5') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2024:na:6:part:b:asset:fdb5fcb015cd'), 'audit', 'Diagrammet viser transportprocesser i distale tubuli og samlerørene i nyrerne, herunder effekten af aldosteron på natrium- og kaliumkanaler. Aldosteron øger antallet af K+-kanaler i den apikale membran og Na+-kanaler i den apikale membran, samt stimulerer Na+-K+-pumpen i den basolaterale membran, hvilket fremmer aktiv transport af Na+ fra tubuluslumen til vævsvæsken og videre til kapillærlumen. Dette illustrerer, hvordan aldosteron reducerer Na+ udskillelse ved at øge reabsorptionen via specifikke proteiner i cellemembraner i distale tubulus og samlerør.', true, 0.95, '', 'legacy', null, null, 'legacy', 'human:short:2024:na:6:part:b:asset:fdb5fcb015cd:audit:legacy', 'figure-audit', 'figure-audit-v1', 'f3a0618de44fb55d43be5b909eb7ca8a896a8ead0326996904d9779e6cf1354a') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2022:na:1:part:a:asset:bb35275a1598'), 'audit', 'Billedet viser en mitochondrie med to membraner: den yderste membran og den inderste membran, som er foldet indad i cristae. Dette illustrerer mitochondriets struktur, men nævner ikke det vigtigste produkt, som mitochondriet producerer, nemlig ATP.', false, 0.7, 'Billedet beskriver kun mitochondriets struktur og nævner ikke det vigtigste produkt (ATP), som mitochondriet producerer, hvilket var en del af spørgsmålet.', 'legacy', null, null, 'legacy', 'human:short:2022:na:1:part:a:asset:bb35275a1598:audit:legacy', 'figure-audit', 'figure-audit-v1', '48f09e1b4294b8a0c4bf813cdb0c858f6a6702ada0f696f781e43dba27035e3c') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2022:na:2:part:p1:asset:ca66994cfd01'), 'audit', 'Illustrationen viser hjertets fire kamre: højre atrium, højre ventrikel, venstre atrium og venstre ventrikel. De store kar, der fører til og fra hvert kammer, er markeret. V. cava superior og V. cava inferior fører blod til højre atrium. Fra højre ventrikel fører Aa. pulmonales blod til lungerne. Vv. pulmonales fører iltet blod til venstre atrium. Fra venstre ventrikel fører aorta blod ud til kroppen. Klapper som trikuspidalklappen, pulmonalklappen, bikuspidalklappen og aortaklappen er også vist, hvilket understøtter forståelsen af blodets flow gennem hjertet.', true, 0.95, '', 'legacy', null, null, 'legacy', 'human:short:2022:na:2:part:p1:asset:ca66994cfd01:audit:legacy', 'figure-audit', 'figure-audit-v1', '9e32894d09bf96c206b8f4d891a90f4a225d9321a119f70a1eeb257cdc7771bd') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
