insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2021:na:1:part:p1:asset:a92d557df0d6'), 'audit', 'Illustrationen viser en celle med det ru (kornede) endoplasmatiske reticulum og Golgi-apparatet markeret. Det ru endoplasmatiske reticulum (blå struktur) er forbundet med cellekernen og har ribosomer på overfladen, hvilket indikerer dets rolle i proteinsyntese. Golgi-apparatet (rød struktur) er vist som en serie af membranøse sække, der modtager vesikler fra det ru endoplasmatiske reticulum, bearbejder og pakker proteiner, og sender dem videre ud af cellen. Illustrationens fokus på disse organeller og deres interaktion understøtter en forklaring af deres funktioner.', true, 0.95, '', 'legacy', null, null, 'legacy', 'human:short:2021:na:1:part:p1:asset:a92d557df0d6:audit:legacy', 'figure-audit', 'figure-audit-v1', 'b730af54ab3a0226a15650f7c4e8a1fb5c56fe6c87d8b868c532c890a12ab18d') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2021:na:2:part:p1:asset:5de28b27f41b'), 'audit', 'Billedet viser en skematisk fremstilling af, hvordan et aktionspotentiale bevæger sig gennem T-rør (transversale rør) og påvirker det sarkoplasmatiske reticulum i en skeletmuskelcelle. Aktionspotentialet (markeret med rød pil) bevæger sig ned gennem T-røret (1), hvilket fører til en signaloverførsel til det sarkoplasmatiske reticulum (2). Dette resulterer i frigivelse af calciumioner (Ca2+) fra det sarkoplasmatiske reticulum til cytosolen (3). Calciumionerne aktiverer muskelfiberen til kontraktion. Calciumionerne pumpes derefter tilbage ind i det sarkoplasmatiske reticulum ved hjælp af ATP (4), hvilket afslutter signalet. Illustrationens trin og elementer stemmer overens med kæden af begivenheder fra aktionspotentiale til calciumstigning i muskelfiberen.', true, 0.9, 'Billedet viser ikke direkte den neuromuskulære synapse eller acetylcholinfrigivelsen, som er det første trin i kæden fra aktionspotentiale til calciumstigning. Fokus er på T-rør og sarkoplasmatisk reticulum, men det overordnede forløb er korrekt illustreret.', 'legacy', null, null, 'legacy', 'human:short:2021:na:2:part:p1:asset:5de28b27f41b:audit:legacy', 'figure-audit', 'figure-audit-v1', '81a26e81bfc564dfeba90e70770dd5bcd9667a4cdb6dabeccc6e8616fb8b6f05') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2021:na:3:part:a:asset:8fb111f69935'), 'audit', 'Diagrammet viser blodets kredsløb gennem hjertet og kroppen. Blodet kommer ind i hjertet via vena cava superior eller inferior til højre atrium, videre til højre ventrikel, derfra til lungearterierne og videre til lungerne. Fra lungerne returnerer blodet via lungevenerne til venstre atrium, videre til venstre ventrikel, og ud gennem aorta til kroppen. Diagrammet inkluderer også detaljer om blodkarrene i forskellige organer og kapillærer.', true, 0.95, 'Ingen væsentlige problemer. Diagrammet illustrerer korrekt rækkefølgen af kamre og blodkar fra vena cava til aorta som efterspurgt.', 'legacy', null, null, 'legacy', 'human:short:2021:na:3:part:a:asset:8fb111f69935:audit:legacy', 'figure-audit', 'figure-audit-v1', '5ae16a07342b9e6037fa8e28cd05c0b878a20c00d40f4d4055effa2dcec4e471') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
