insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2020:ordinær:3:part:a:asset:ba91d8a7668d'), 'audit', 'Grafen viser sammenhængen mellem sarkomerlængde (i mikrometer) og kontraktionskraft (i procent af maksimum). Kurven stiger fra lav kontraktionskraft ved kort sarkomerlængde, når sarkomerlængden øges, når kontraktionskraften maksimum omkring 2,0 til 2,5 mikrometer, hvorefter kontraktionskraften falder igen ved længere sarkomerlængder. Diagrammet inkluderer illustrationer af sarkomerer ved forskellige længder, der viser overlapningen mellem aktin- og myosinfilamenter, som forklarer variationen i kontraktionskraft. Dette illustrerer den fysiologiske sammenhæng mellem sarkomerlængde og udvikling af kontraktionskraft i muskler.', true, 0.95, '', 'legacy', null, null, 'legacy', 'human:short:2020:ordinær:3:part:a:asset:ba91d8a7668d:audit:legacy', 'figure-audit', 'figure-audit-v1', '6da8bce8d274ea17d91c933b4c79d51fddcd375c99711736a44d1eaedb553c3d') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2020:ordinær:4:part:p1:asset:ac6b520f7ed8'), 'audit', 'Illustrationen viser hjertets anatomi med blodets vej fra vena cava superior og inferior gennem hjertets kamre og klapper til aorta. Blodet kommer ind i højre atrium via vena cava superior og inferior, passerer gennem trikuspidalklappen til højre ventrikel, videre gennem pulmonalklappen til arteria pulmonalis, der fører blodet til lungerne. Fra lungerne vender blodet tilbage via vv. pulmonales til venstre atrium, passerer gennem bikuspidalklappen (mitralklappen) til venstre ventrikel, og endelig gennem aortaklappen til aorta, som fører blodet ud til kroppen. Illustrationerne b og c viser detaljer af hjerteklapperne.', true, 0.95, 'Spørgsmålet mangler eksplicit tekst, men billedet indeholder alle nødvendige anatomiske strukturer og klapper til at beskrive blodets vej fra vena cava til aorta.', 'legacy', null, null, 'legacy', 'human:short:2020:ordinær:4:part:p1:asset:ac6b520f7ed8:audit:legacy', 'figure-audit', 'figure-audit-v1', '2bff7d5ca68a0bba68d27d51fc767239c6d052b1181b8c1a7d75fb0924f423d5') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2020:ordinær:4:part:p1:asset:64df7da68c54'), 'audit', 'Illustrationen viser hjertets pumpecyklus i fem trin (a-e), hvor blodets bevægelse gennem kamre, klapper og kar beskrives. Blodet strømmer fra vena cava superior og inferior ind i højre atrium, gennem AV-klappen til højre ventrikel (a). Atrierne kontraherer og presser mere blod ned i ventriklerne (b). Ventriklerne trækker sig sammen, hvilket lukker AV-klapperne (c). Trykket i ventriklerne stiger, hvilket åbner klapperne til arterierne, og blodet strømmer ud i aorta og pulmonalis (d). Ventriklerne slapper af, arterieklapperne lukkes, og AV-klapperne åbnes igen for at starte en ny cyklus (e). Dette svarer til rækkefølgen af kamre, klapper og kar blodet passerer fra vena cava til aorta.', true, 0.95, 'Der er ingen direkte tekst, der beskriver rækkefølgen i ét samlet svar, men illustrationens trin og forklaringer dækker processen fuldstændigt og korrekt.', 'legacy', null, null, 'legacy', 'human:short:2020:ordinær:4:part:p1:asset:64df7da68c54:audit:legacy', 'figure-audit', 'figure-audit-v1', '925d448f81873942e551cdc15ecd52b27c24008b0ee422c29c872946b504fd41') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
