insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2018:ordinær:1:part:p1:asset:f5e42127bafa'), 'audit', 'Billedet viser en trinvis proces for nedbrydning af glukose under anaerobe forhold. Først omdannes glukose til to pyruvatmolekyler via glykolyse, hvilket giver en netto gevinst på to ATP og to NADH. Dernæst omdannes pyruvat til mælkesyre (laktat), hvor NADH bruges, og der frigives H+ ioner, som danner laktat. Diagrammet viser også, at ATP-udbyttet er lille sammenlignet med aerob forbrænding, da kun 2 ATP produceres pr. glukosemolekyle under anaerobe forhold.', true, 0.95, 'Der står ikke eksplicit i billedet, at ATP-udbyttet er lille i forhold til aerob forbrænding, men det kan udledes ud fra den angivne ATP-mængde (2 ATP).', 'legacy', null, null, 'legacy', 'human:short:2018:ordinær:1:part:p1:asset:f5e42127bafa:audit:legacy', 'figure-audit', 'figure-audit-v1', '5dc63f1e01fee67706a73dcce95cd2e610e512948eb49778ec331d7eb21d5068') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
