insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2018:ordinær:4:part:p1:asset:89cb55190b16'), 'audit', 'Diagrammet viser renin-angiotensin-aldosteron-systemet (RAAS) og dets rolle i reguleringen af blodtryk ved hypotension. Det starter med, at nyrerne registrerer faldende blodtryk og øger reninsekretionen (1). Renin omdanner angiotensinogen fra leveren til angiotensin I (2). Angiotensin I omdannes i lungerne til angiotensin II via angiotensin-konverterende enzym (3). Angiotensin II virker som en potent vasokonstriktor, hvilket øger blodtrykket (4). Det stimulerer også binyrebarken til at udskille aldosteron (5), som øger natriumreabsorptionen i nyrerne, hvilket mindsker urinvolumen og øger blodvolumen og dermed blodtrykket (6). Diagrammet illustrerer klart, hvordan RAAS bidrager til at reetablere normalt blodtryk ved hypotension.', true, 1.0, '', 'legacy', null, null, 'legacy', 'human:short:2018:ordinær:4:part:p1:asset:89cb55190b16:audit:legacy', 'figure-audit', 'figure-audit-v1', 'cbd42ba4b8f648a027b8f49e21cdc0da9de34102b66941f849b95279b5861fd1') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2018:ordinær:5:part:p1:asset:0a4fe5936260'), 'audit', 'Billedet viser en detaljeret anatomisk og histologisk opbygning af tyndtarmens væg. Makroskopisk ses cirkulære folder, epitel, submucosa, cirkulært muskel lag, longitudinelt muskel lag og serosa. Mikroskopisk vises villi med epitelceller, blodkapillærer, lymfekar (lacteal) og mikrovilli på epitelcellernes overflade. Der er også illustration af duodenale kirtler og intestinale kirtler. Billedet dækker både makroskopiske strukturer og mikroskopiske detaljer af tyndtarmens væg.', true, 0.95, 'Ingen væsentlige problemer. Billedet matcher spørgsmålet godt ved at vise både makroskopisk og mikroskopisk opbygning af tyndtarmens væg.', 'legacy', null, null, 'legacy', 'human:short:2018:ordinær:5:part:p1:asset:0a4fe5936260:audit:legacy', 'figure-audit', 'figure-audit-v1', '19a9ca1e1c431a2b6816b9c6f63fa2694161c1d5b405a7adc2eab6fd33090102') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2018:ordinær:6:part:p1:asset:9342daf12812'), 'audit', 'Billedet viser en detaljeret illustration af en alveole og den respiratoriske membran. Øverst ses alveolens opbygning med makrofager, luftplads, cellekerne, mitokondrier, surfaktant-secernerende celler, alveolepitel (enlaget pladeepitel), samt kapillærendotel med røde blodlegemer. Nederst er en forstørret sektion, der viser alveolevæske med surfaktant, alveolepitel, basalmembran af alveolepitel, interstitielt rum, basalmembran af kapillærendotel, kapillærendotel, og diffusion af ilt (O2) og kuldioxid (CO2) over den respiratoriske membran. Røde blodlegemer i kapillæren er også markeret. Illustrationerne forklarer strukturen og funktion af alveolen og den respiratoriske membran i forbindelse med gasudveksling.', true, 0.95, '', 'legacy', null, null, 'legacy', 'human:short:2018:ordinær:6:part:p1:asset:9342daf12812:audit:legacy', 'figure-audit', 'figure-audit-v1', '00602eb5794b880eb5a4669c0acafb9752fb96173335475b32f07b757f9ee56e') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2018:sygeeksamen:1:part:p1:asset:6753fbf75523'), 'audit', 'Illustrationen viser processen i den neuromuskulære kontakt ved en skeletmuskel. Et aktionspotentiale ankommer til den presynaptiske terminal, hvilket får Ca2+ kanaler til at åbne. Calciumioner (Ca2+) trænger ind i den presynaptiske terminal og initierer frigivelsen af neurotransmitteren acetylcholin (ACh) fra synaptiske vesikler ud i den synaptiske kløft. ACh diffunderer over kløften og binder til ACh-receptorer på den postsynaptiske muskelmembran (sarcolemma), hvilket åbner Na+ kanaler. Natriumioner (Na+) diffunderer ind i muskelfiberen, hvilket fører til depolarisering af membranen og generering af et postsynaptisk aktionspotentiale, hvis tærskelværdien nås.', true, 0.95, 'Ingen væsentlige problemer. Teksten og billedet beskriver klart og korrekt processen fra aktionspotentiale i den presynaptiske terminal til generering af aktionspotentiale i muskelcellen.', 'legacy', null, null, 'legacy', 'human:short:2018:sygeeksamen:1:part:p1:asset:6753fbf75523:audit:legacy', 'figure-audit', 'figure-audit-v1', '9015fb9098f994dbc70a80ab0c2154c18804be54620ab7ceb5240e5ffd03b983') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
