insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2018:sygeeksamen:3:part:p1:asset:a009eb7feb1c'), 'audit', 'Grafen viser et spirogram med volumen (ml) på y-aksen og tid på x-aksen. Den illustrerer forskellige lungevolumina: Tidalvolumen i hvile (500 ml), inspiratorisk reservevolumen (3.000 ml), eksspiratorisk reservevolumen (1.500 ml) og residualvolumen (1.000 ml). Vitalkapaciteten (5.000 ml) er markeret som summen af inspiratorisk reservevolumen, tidalvolumen og eksspiratorisk reservevolumen. Volumina er korrekt navngivet og omtrentlige størrelser er angivet i overensstemmelse med typiske værdier for en voksen person i hvile. Spirogrammet viser også normale vejrtrækninger, en maksimal dyb indånding og en maksimal dyb udånding som beskrevet i spørgsmålet.', true, 0.95, '', 'legacy', null, null, 'legacy', 'human:short:2018:sygeeksamen:3:part:p1:asset:a009eb7feb1c:audit:legacy', 'figure-audit', 'figure-audit-v1', '2d52d8022b612ce0ae44723b39601221030164d4a757552512bae63d9aa2bde4') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2018:sygeeksamen:4:part:p1:asset:b9dd2e00149b'), 'audit', 'Billedet viser hjertets anatomi med en detaljeret illustration af blodets flow gennem hjertets kamre og kar. Det starter ved vena cava superior og inferior, går til højre atrium, passerer tricuspidalklappen til højre ventrikel, videre gennem pulmonal semilunarklappen til pulmonal trunk og pulmonalarterier, der fører blod til lungerne. Fra lungerne returnerer blodet via pulmonalvenerne til venstre atrium, passerer bicuspidalklappen til venstre ventrikel, og pumpes ud gennem aortaklappen til aorta og videre til kroppens væv. Diagrammet indeholder også labels for hjerteklapper og blodkar, og viser både systemisk og pulmonal cirkulation.', true, 1.0, '', 'legacy', null, null, 'legacy', 'human:short:2018:sygeeksamen:4:part:p1:asset:b9dd2e00149b:audit:legacy', 'figure-audit', 'figure-audit-v1', '8239188f6607f5c3be690b444ee1cd96ba423348bf0c5d627f0db7c7a4ed2945') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
insert into public.asset_annotations (asset_id, annotation_type, text, match, confidence, issues, source_type, llm_model, llm_prompt_version, llm_output_version, source_key, source_system, source_version, content_hash) values ((select id from public.item_assets where source_key = 'human:short:2018:sygeeksamen:5:part:p1:asset:725c3e2b49a9'), 'audit', 'Illustrationen viser, hvordan nerveceller i hypothalamus regulerer hormonsekretion i den forreste hypofyselap. Hypothalamiske neuroner frigiver stimulerende (grønne cirkler) og hæmmende hormoner (røde cirkler), som passerer gennem hypothalamohypofysial portalsystemet til den forreste hypofyse. Her forlader disse hormoner kapillærer og påvirker hypofyseceller til enten at stimulere eller hæmme frigivelsen af hypofysehormoner (gule firkanter). Disse hypofysehormoner transporteres derefter via blodet til målorganer eller endokrine kirtler (markeret med grøn pil). Diagrammet forklarer således, hvordan hypothalamus nerveceller kan stimulere eller hæmme hormonsekretion i den forreste hypofyse.', true, 0.95, '', 'legacy', null, null, 'legacy', 'human:short:2018:sygeeksamen:5:part:p1:asset:725c3e2b49a9:audit:legacy', 'figure-audit', 'figure-audit-v1', '68b1a38dec75023a46d5f43a0a571c26239350f31cb05298d26e6899b766413a') on conflict (source_key) do update set annotation_type = excluded.annotation_type, text = excluded.text, match = excluded.match, confidence = excluded.confidence, issues = excluded.issues, source_type = excluded.source_type, llm_model = excluded.llm_model, llm_prompt_version = excluded.llm_prompt_version, llm_output_version = excluded.llm_output_version, source_system = excluded.source_system, source_version = excluded.source_version, content_hash = excluded.content_hash where public.asset_annotations.content_hash is distinct from excluded.content_hash;
